INSERT INTO `article_versions`(`version_id`, `article_id`, `author_id`, `version_number`, `content`, `create_date`, `option_type`, `operator_id`) VALUES (13, 4, 10, 1, '<p style=\"line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\">新华社北京5月7日电 外交部发言人7日就当前印巴局势答记者问。</span></p><p style=\"line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\">　　有记者问：近期，印度和巴基斯坦之间的紧张局势持续升级。印度7日凌晨对巴基斯坦境内目标进行了军事打击，巴方作出强烈反应。中方对此有何评论？</span></p><p style=\"line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\">　　发言人表示：“中方对今天凌晨印度的军事行动表示遗憾，对目前事态发展感到担忧。印度和巴基斯坦是搬不走的邻居，也都是中国的邻国。中方反对一切形式的恐怖主义。我们呼吁印巴双方以和平稳定大局为重，保持冷静克制，避免采取使局势进一步复杂化的行动。”</span></p><p style=\"text-align: right; line-height: 1;\">(责编：牛镛、岳弘彬)</p>', '2025-05-07 17:40:01', 'UPDATE_CONTENT', 10);
INSERT INTO `article_versions`(`version_id`, `article_id`, `author_id`, `version_number`, `content`, `create_date`, `option_type`, `operator_id`) VALUES (14, 4, 10, 2, '<p style=\"line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\">新华社北京5月7日电 外交部发言人7日就当前印巴局势答记者问。</span></p><p style=\"line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\"> 有记者问：近期，印度和巴基斯坦之间的紧张局势持续升级。印度7日凌晨对巴基斯坦境内目标进行了军事打击，巴方作出强烈反应。中方对此有何评论？</span></p><p style=\"line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\"> 发言人表示：“中方对今天凌晨印度的军事行动表示遗憾，对目前事态发展感到担忧。印度和巴基斯坦是搬不走的邻居，也都是中国的邻国。中方反对一切形式的恐怖主义。我们呼吁印巴双方以和平稳定大局为重，保持冷静克制，避免采取使局势进一步复杂化的行动。”</span></p><p style=\"text-align: right; line-height: 1;\">(责编：牛镛、岳弘彬)</p>', '2025-05-07 17:46:46', 'UPDATE_CONTENT', 10);
