INSERT INTO `articles`(`article_id`, `title`, `content`, `publish_date`, `update_date`, `author_id`, `status`, `is_deleted`, `last_operator_id`, `like_num`, `coin_num`, `favorit_num`, `share_num`, `head_image_url`) VALUES (2, '一习话丨“建立健全更加合理高效的教育资源配置机制”', '<p style=\"text-align: center;\"><img src=\"http://seunewsbac.pgrm.cc/static/news-images/20250328233745490_224cd9b7.png\" alt=\"\" data-href=\"\" style=\"width: 50%;\"></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 楷体_GB2312;\">“要着眼现代化需求，适应人口结构变化，统筹基础教育、高等教育、职业教育，统筹政府投入和社会投入，建立健全更加合理高效的教育资源配置机制。”3月6日，习近平总书记看望参加全国政协十四届三次会议的民盟、民进、教育界委员，参加联组会，听取意见和建议并发表重要讲话。<br></span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 楷体_GB2312;\">当天，总书记向委员们讲了一个故事：“当年我给梁家河村里还集资办了小学，挺高兴的。后来为什么没了呢？也是好事，为提升教学质量，集中到乡镇去办了。那也是学校结构的一次调整。”梁家河村小学从无到有、再从有到整合的历史变迁，反映的正是我国基础教育资源随时代发展、人口结构等变化，不断优化调整配置的动态过程。</span></p><p style=\"text-align: center;\"><img src=\"http://seunewsbac.pgrm.cc/static/news-images/20250328225611226_f4246638.jpg\" alt=\"\" data-href=\"\" style=\"width: 50%;\"></p><p style=\"text-align: center;\"><span style=\"font-family: 楷体_GB2312;\">学生聆听线上课堂课程</span></p><p style=\"text-align: center;\"><img src=\"http://seunewsbac.pgrm.cc/static/news-images/20250328233811309_2857d18d.jpg\" alt=\"\" data-href=\"\" style=\"width: 50%;\"></p><p style=\"text-align: center;\"><span style=\"font-family: 楷体_GB2312;\">职业学校的学生在进行工业机器人实操</span></p><p><br></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 楷体_GB2312;\">“致天下之治者在人才，成天下之才者在教化。”教育资源的合理高效配置，是关乎教育公平、教育质量以及国家和民族未来发展的关键问题。当前，我国已建成世界上规模最大的教育体系，只有通过建立健全更加合理高效的教育资源配置机制，加快推进教育强国建设，才能更好发挥教育对科技和人才的支撑作用，为高质量发展提供高素质人才资源。</span></p><p><span style=\"font-family: 楷体_GB2312;\">监制：张军 于锋<br></span></p><p><span style=\"font-family: 楷体_GB2312;\">统筹：伍刚 王薇<br></span></p><p><span style=\"font-family: 楷体_GB2312;\">策划：田甜</span></p><p><span style=\"font-family: 楷体_GB2312;\">视频：郑平平 孙晓钰（实习）<br></span></p><p><span style=\"font-family: 楷体_GB2312;\">编辑:曹宇 </span></p>', '2025-03-12 11:37:30', '2025-05-08 21:17:21', 1, 'PUBLISHED', 0, 1, 3, 8, 3, 90103, 'http://seunewsbac.pgrm.cc/static/news-images/20250419230732801_338c1c11.png');
INSERT INTO `articles`(`article_id`, `title`, `content`, `publish_date`, `update_date`, `author_id`, `status`, `is_deleted`, `last_operator_id`, `like_num`, `coin_num`, `favorit_num`, `share_num`, `head_image_url`) VALUES (3, '测试新闻', '<p>测试新闻</p><p><br></p><p><br></p><p><br></p><p style=\"text-align: center;\"><img src=\"http://seunewsbac.pgrm.cc/static/news-images/20250313220252709_18037439.png\" alt=\"\" data-href=\"\" style=\"width: 100%;\"></p><p style=\"text-align: center;\"><strong>柯南</strong></p>', '2025-03-13 11:11:01', '2025-05-07 22:00:41', 1, 'PUBLISHED', 0, 1, 1, 0, 0, 0, 'http://seunewsbac.pgrm.cc/static/news-images/20250419203747380_33cf26ea.png');
INSERT INTO `articles`(`article_id`, `title`, `content`, `publish_date`, `update_date`, `author_id`, `status`, `is_deleted`, `last_operator_id`, `like_num`, `coin_num`, `favorit_num`, `share_num`, `head_image_url`) VALUES (4, '外交部发言人就当前印巴局势答记者问', '<p style=\"text-indent: 2em; line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\">新华社北京5月7日电 外交部发言人7日就当前印巴局势答记者问。</span></p><p style=\"text-indent: 2em; line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\">有记者问：近期，印度和巴基斯坦之间的紧张局势持续升级。印度7日凌晨对巴基斯坦境内目标进行了军事打击，巴方作出强烈反应。中方对此有何评论？</span></p><p style=\"text-indent: 2em; line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\">发言人表示：“中方对今天凌晨印度的军事行动表示遗憾，对目前事态发展感到担忧。印度和巴基斯坦是搬不走的邻居，也都是中国的邻国。中方反对一切形式的恐怖主义。我们呼吁印巴双方以和平稳定大局为重，保持冷静克制，避免采取使局势进一步复杂化的行动。”</span></p><p style=\"text-align: right; line-height: 1;\"><span style=\"font-size: 19px; font-family: 楷体_GB2312;\">(责编：牛镛、岳弘彬)</span></p>', '2025-05-07 03:36:18', '2025-05-08 15:08:49', 10, 'PUBLISHED', 0, 10, 2, 1, 1, 0, 'http://seunewsbac.pgrm.cc/static/news-images/20250507171922686_17aea23c.jpg');
